import "./App.css";
import Cities from "./data";
import { useState } from "react";

function App() {
  const [activeCity, setActiveCity] = useState(0);
  const handleClick = (e) => {
    // console.log(window.getComputedStyle(e.target));

    // console.log(window.getComputedStyle(e.target).transformOrigin);
    console.log(window.getComputedStyle(e.target).width);
    //transformOrigin
    //webkitLogicalHeight
    //webkitLogicalWidth
    //webkitPerspectiveOrigin
    //webkitTransformOrigin
    //width
    setActiveCity(e.target.innerHTML);
  };
  return (
    <div className="App">
      <div className="cities">
        {Cities.map((city, section) => (
          <p
            key={section}
            onClick={handleClick}
            className={activeCity === city.label ? "active" : ""}
          >
            {city.label}
          </p>
        ))}
      </div>

      <hr

      />
    </div>
  );
}

export default App;
