import "./App.css";
import Cities from "./data";
import { useState } from "react";

function App() {
  const [activeCity, setActiveCity] = useState("");
  const handleClick = (e) => {
    setActiveCity(e.target.innerHTML)
    e.target.className = "active";
  };
  return (
    <div className="App">
      <div className="cities">
        {Cities.map((city, section) => (
          <p key={section} onClick={handleClick}>
            {city.label}
          </p>
        ))}
      </div>

      <hr
        style={{
          margin: "5px",
          padding: "1px",
          border: "0.05px solid lightgrey",
          backgroundColor: "lightgrey",
          height: 0,
          borderColor: "#000000",
        }}
      />
    </div>
  );
}

export default App;
