import "./App.css";
import Cities from "./data";
import { useState } from "react";

function App() {
  const [activeCity, setActiveCity] = useState(0);

  const handleClick = (e) => {
    setActiveCity(e.target.innerHTML);
  };
  let date, time;
  if (activeCity != 0) {
    let options = {
        timeZone: "Africa/Khartoum",
        year: "numeric",
        month: "numeric",
        day: "numeric",
        hour: "numeric",
        minute: "numeric",
        second: "numeric",
      },
      formatter = new Intl.DateTimeFormat([], options);
    date = formatter.format(new Date());
    time = date.split(",");
    // time = date.getTime();
    // console.log(time);
  }

  return (
    <div className="App">
      <div className="cities">
        {Cities.map((city, section) => (
          <p
            key={section}
            onClick={handleClick}
            className={activeCity === city.label ? "active" : ""}
          >
            {city.label}
          </p>
        ))}
      </div>

      <hr />
      {activeCity != 0 && <p>{time}</p>}
    </div>
  );
}

export default App;
