import "./App.css";
import Cities from "./data";
import { useState } from "react";

function App() {
  const [activeCity, setActiveCity] = useState(0);

  async function fetchGeoParams() {
    const response = await fetch(
      `http://api.positionstack.com/v1/forward?access_key=11f0c060cdd0438457b4f4b8b20ccb83&query=${activeCity}`
    );
    const jsonData = await response.json();
    return jsonData.data;
  }
  const handleClick = (e) => {
    setActiveCity(e.target.innerHTML);
    const geoParams = fetchGeoParams();
    console.log(activeCity);
    console.log(geoParams);
  };
  let options = {
    timeZone: 'Europe/London',
    year: 'numeric',
    month: 'numeric',
    day: 'numeric',
    hour: 'numeric',
    minute: 'numeric',
    second: 'numeric',
  },
  formatter = new Intl.DateTimeFormat([], options);

console.log(formatter.format(new Date()));
  return (
    <div className="App">
      <div className="cities">
        {Cities.map((city, section) => (
          <p
            key={section}
            onClick={handleClick}
            className={activeCity === city.label ? "active" : ""}
          >
            {city.label}
          </p>
        ))}
      </div>

      <hr />
    </div>
  );
}

export default App;
