import "./App.css";
import Cities from "./data";
import { useState } from "react";

function App() {
  const [activeCity, setActiveCity] = useState(0);
  const handleClick = (e) => {
    console.log(window.getComputedStyle(e.target));
    // console.log(window.getComputedStyle(e.target.blockSize));
    // console.log(window.transformOrigin(e.target.blockSize));
    //transformOrigin
    //webkitLogicalHeight
    //webkitLogicalHeight
    setActiveCity(e.target.innerHTML);
  };
  return (
    <div className="App">
      <div className="cities">
        {Cities.map((city, section) => (
          <p
            key={section}
            onClick={handleClick}
            className={activeCity === city.label ? "active" : ""}
          >
            {city.label}
          </p>
        ))}
      </div>

      <hr
        style={{
          margin: "5px",
          border: "0.05px solid lightgray",
          backgroundColor: "lightgrey",
          color: "lightgrey",
          borderColor: "#000000",
        }}
      />
    </div>
  );
}

export default App;
