import "./App.css";
import Cities from "./data";
import { useState } from "react";

function App() {
  const [activeCity, setActiveCity] = useState(0);
  fetch('https://maps.googleapis.com/maps/api/geocode/json?&address=paris').then
  const handleClick = (e) => {
    setActiveCity(e.target.innerHTML);
  };
  return (
    <div className="App">
      <div className="cities">
        {Cities.map((city, section) => (
          <p
            key={section}
            onClick={handleClick}
            className={activeCity === city.label ? "active" : ""}
          >
            {city.label}
          </p>
        ))}
      </div>

      <hr />
    </div>
  );
}

export default App;
